import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import TopNav from "../components/TopNav";
import Container from "../components/Container";
import { apiGet } from "../utils/api";

export default function CommunityPage() {
  const { communityId } = useParams();   // ✅ correct param name

  const [community, setCommunity] = useState(null);
  const [media, setMedia] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function loadData() {
      try {
        // ✅ CORRECT FETCH — use communityId
        const communityRes = await apiGet(`/communities/${communityId}`);
        setCommunity(communityRes);

        const mediaRes = await apiGet(`/media?communityId=${communityId}`);
        setMedia(mediaRes);
      } catch (err) {
        setError("Failed to load community details.");
      } finally {
        setLoading(false);
      }
    }
    loadData();
  }, [communityId]);

  if (loading)
    return <div className="text-center mt-20 text-xl text-gray-700">Loading community...</div>;

  if (error)
    return <div className="text-center mt-20 text-red-500">{error}</div>;

  return <div>...your UI...</div>;
}
