import axios from "axios";

export const api = axios.create({
  baseURL: "http://localhost:5000/api",
});

// ===================
// COMMUNITY APIs
// ===================

// Get all communities
export async function getCommunities() {
  try {
    const res = await fetch("http://localhost:5000/api/communities"); // <-- add /api
    return await res.json();
  } catch (err) {
    console.error("Error fetching communities:", err);
    return [];
  }
}
export const BASE_URL = "http://localhost:5000/api";  // Change to your real backend

export async function apiGet(endpoint) {
  const res = await fetch(`${BASE_URL}${endpoint}`);
  return res.json();
}

export async function apiPost(endpoint, data) {
  const res = await fetch(`${BASE_URL}${endpoint}`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}


// Get single community
export const getCommunityById = async (id) => {
  try {
    const res = await api.get(`/communities/${id}`);
    return res.data;
  } catch (err) {
    console.error("Error fetching community:", err);
    return null;
  }
};

// Get media for a community
export const getMediaByCommunity = async (communityId) => {
  try {
    const res = await api.get(`/media/community/${communityId}`);
    return res.data;
  } catch (err) {
    console.error("Error fetching media:", err);
    return [];
  }
};
