// --------------------------
// 5. Search and Filter Page
// --------------------------
export function SearchPage() {
  const [query, setQuery] = React.useState("");
  const results = React.useMemo(() => {
    if (!query) return DUMMY_COMMUNITIES;
    return DUMMY_COMMUNITIES.filter((c) => c.name.toLowerCase().includes(query.toLowerCase()));
  }, [query]);

  return (
    <div>
      <TopNav />
      <Container>
        <h2 className="text-2xl font-semibold mb-4">Search</h2>
        <input value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Search communities or media" className="w-full p-3 border rounded" />

        <div className="mt-6 grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {results.map((c) => (
            <Link key={c.id} to={`/community/${c.id}`} className="block p-4 border rounded hover:shadow">
              <h3 className="font-medium">{c.name}</h3>
              <p className="text-sm text-gray-600">{c.description}</p>
            </Link>
          ))}
        </div>
      </Container>
    </div>
  );
}

// --------------------------
// 6. Media Detail / Player Page
// --------------------------
export function MediaDetailPage() {
  const { communityId, mediaId } = useParams();
  const media = (DUMMY_MEDIA[communityId] || []).find((m) => m.id === mediaId) || null;

  if (!media) {
    return (
      <div>
        <TopNav />
        <Container>
          <h3 className="text-lg font-medium">Media not found</h3>
        </Container>
      </div>
    );
  }

  return (
    <div>
      <TopNav />
      <Container>
        <h2 className="text-2xl font-semibold">{media.title}</h2>
        <p className="text-sm text-gray-600 mb-4">Type: {media.type}</p>

        <div className="border rounded p-4">
          {media.type === "image" && <img src={media.url} alt={media.title} className="w-full object-contain" />}
          {media.type === "audio" && <audio controls src={media.url} className="w-full" />}
          {media.type === "video" && <video controls src={media.url} className="w-full" />}
        </div>

        {media.transcript && (
          <section className="mt-4">
            <h4 className="font-medium">Transcript</h4>
            <pre className="bg-gray-50 p-3 rounded text-sm">{media.transcript}</pre>
          </section>
        )}
      </Container>
    </div>
  );
}

