// @ts-nocheck

import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useParams } from "react-router-dom";

/*
  Kaavish - Living Archives
  Single-file React + Tailwind sample app that includes core screens from the proposal.

  - Simple, student-friendly structure
  - Tailwind utility classes used for styling (no external CSS imports required)
  - Each screen is a functional component you can copy into your project files
  - Replace placeholder data / API calls with real endpoints later
*/

// --------------------------
// Dummy data (replace with API/MongoDB calls)
// --------------------------
const DUMMY_COMMUNITIES = [
  { id: "kolhi", name: "Kolhi (Tharparkar)", description: "A short intro to the Kolhi community.", thumbnail: "/images/kolhi-thumb.jpg" },
  { id: "example", name: "Example Community", description: "Sample community for demo.", thumbnail: "/images/example-thumb.jpg" },
];

const DUMMY_MEDIA = {
  kolhi: [
    { id: "img1", type: "image", title: "Traditional Tattoo", url: "/media/tattoo.jpg", transcript: null },
    { id: "aud1", type: "audio", title: "Oral History - A", url: "/media/oral-a.mp3", transcript: "(transcript will be shown here)" },
    { id: "vid1", type: "video", title: "Kolhi Song", url: "/media/song.mp4", transcript: "(video transcript)" },
  ],
};

// --------------------------
// Layout / Shared components
// --------------------------
function TopNav({ isAdmin }) {
  return (
    <header className="bg-white shadow-sm">
      <div className="max-w-6xl mx-auto px-5 py-5 flex items-center justify-between">
        <Link to="/" className="text-xl font-semibold">Living Archives</Link>
        <nav className="space-x-4">
          <Link to="/explore" className="text-sm">Explore</Link>
          <Link to="/about" className="text-sm">About</Link>
          {isAdmin ? <Link to="/admin" className="text-sm font-medium">Admin</Link> : null}
        </nav>
      </div>
    </header>
  );
}

function Container({ children }) {
  return <main className="max-w-6xl mx-auto px-4 py-6">{children}</main>;
}

// --------------------------
// 1. Landing / Home Page
// --------------------------
export function HomePage() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-50 text-gray-800">
      <TopNav />
      <Container>
        {/* Hero Section */}
        <section className="text-center py-16 px-6 bg-white/70 backdrop-blur-sm rounded-2xl shadow-md border border-indigo-100">
          <h1 className="text-5xl font-extrabold mb-4 bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
            Living Archives
          </h1>
          <p className="text-lg text-gray-700 max-w-2xl mx-auto mb-8 leading-relaxed">
            Preserving the cultural heritage of Sindh through <span className="font-semibold text-indigo-700">stories, sounds, visuals,</span> 
            and <span className="font-semibold text-purple-700">immersive 3D experiences.</span>
          </p>
          <div className="flex justify-center space-x-4">
            <Link
              to="/explore"
              className="px-6 py-3 bg-indigo-600 text-white rounded-full font-medium shadow-sm hover:bg-indigo-700 transition-all"
            >
              Explore Communities
            </Link>
            <Link
              to="/about"
              className="px-6 py-3 border border-indigo-600 text-indigo-700 rounded-full font-medium hover:bg-indigo-50 transition-all"
            >
              Learn More
            </Link>
          </div>
        </section>

        {/* Featured Section */}
        <section className="mt-12 grid md:grid-cols-2 gap-8">
          <div className="p-6 bg-white/60 backdrop-blur-sm rounded-xl shadow border hover:shadow-lg transition-all">
            <h2 className="text-2xl font-semibold text-indigo-700 mb-2">Featured: Kolhi Community</h2>
            <p className="text-gray-600 text-sm mb-4">
              A proof-of-concept collection showcasing traditional tattoos, oral histories, and a 3D tour from Tharparkar.
            </p>
            <Link
              to="/community/kolhi"
              className="inline-block text-indigo-600 font-medium hover:underline"
            >
              View Kolhi Collection 
            </Link>
          </div>

          <div className="p-6 bg-white/60 backdrop-blur-sm rounded-xl shadow border hover:shadow-lg transition-all">
            <h2 className="text-2xl font-semibold text-purple-700 mb-2">About the Project</h2>
            <p className="text-gray-600 text-sm mb-4">
              Developed as part of <span className="font-medium text-indigo-700">Kaavish</span>  this platform uses the 
              <span className="font-medium text-purple-700"> MERN stack</span> to support multimedia archives and 3D NeRF-based visual tours.
            </p>
            <Link
              to="/about"
              className="inline-block text-purple-600 font-medium hover:underline"
            >
              Discover More 
            </Link>
          </div>
        </section>

        {/* Footer */}
        <footer className="mt-16 text-center text-sm text-gray-500">
           {new Date().getFullYear()} Living Archives  A Kaavish Project
        </footer>
      </Container>
    </div>
  );
}




// --------------------------
// 2. Explore Communities Page
// --------------------------
export function ExplorePage() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-50">
      <TopNav />
      <Container>
        <h2 className="text-4xl font-extrabold text-center mb-10 bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
          Explore Communities
        </h2>

        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {DUMMY_COMMUNITIES.map((c) => (
            <Link
              key={c.id}
              to={`/community/${c.id}`}
              className="group block rounded-2xl overflow-hidden shadow-md hover:shadow-xl bg-white/70 backdrop-blur-sm border border-indigo-100 transition-transform transform hover:-translate-y-1 duration-300"
            >
              <div className="relative">
                <img
                  src={c.thumbnail}
                  alt={c.name}
                  className="h-48 w-full object-cover group-hover:scale-105 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>

              <div className="p-5">
                <h3 className="text-xl font-semibold text-indigo-700 mb-1">{c.name}</h3>
                <p className="text-sm text-gray-700 mb-2">{c.description}</p>
                <span className="text-sm text-indigo-500 font-medium group-hover:underline">
                  View Details 
                </span>
              </div>
            </Link>
          ))}
        </div>
      </Container>
    </div>
  );
}


// --------------------------
// 3. Community Detail Page
// --------------------------
function MediaList({ communityId }) {
  const media = DUMMY_MEDIA[communityId] || [];
  return (
    <div className="space-y-3">
      {media.map((m) => (
        <Link key={m.id} to={`/community/${communityId}/media/${m.id}`} className="block p-3 border rounded flex items-center gap-3">
          <div className="w-16 h-12 bg-gray-100 flex items-center justify-center rounded">{m.type}</div>
          <div>
            <div className="font-medium">{m.title}</div>
            <div className="text-xs text-gray-500">{m.type.toUpperCase()}</div>
          </div>
        </Link>
      ))}
    </div>
  );
}
export function CommunityPage() {
  const { communityId } = useParams();
  const community =
    DUMMY_COMMUNITIES.find((c) => c.id === communityId) || {
      name: "Unknown",
      description: "Community details not found.",
      thumbnail: "/images/default.jpg",
    };

  const media = DUMMY_MEDIA[communityId] || [];

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-white to-indigo-50">
      <TopNav />
      <Container>
        {/* Hero Section */}
        <div className="relative overflow-hidden rounded-2xl shadow-lg mb-10">
          <img
            src={community.thumbnail}
            alt={community.name}
            className="w-full h-72 object-cover brightness-75"
          />
          <div className="absolute inset-0 flex flex-col justify-center items-center text-white text-center px-4 bg-black/30">
            <h2 className="text-4xl font-extrabold mb-2 drop-shadow-lg">
              {community.name}
            </h2>
            <p className="max-w-2xl text-sm md:text-base opacity-90">
              {community.description}
            </p>
            <div className="mt-5 space-x-3">
              <Link
                to={`/community/${communityId}/tour`}
                className="inline-block bg-gradient-to-r from-green-500 to-emerald-600 text-white px-5 py-2 rounded-full font-medium shadow hover:scale-105 transition-all"
              >
                Open 3D Tour
              </Link>
              <Link
                to={`/community/${communityId}/media`}
                className="inline-block bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-5 py-2 rounded-full font-medium shadow hover:scale-105 transition-all"
              >
                View Media
              </Link>
            </div>
          </div>
        </div>

        {/* Media Section */}
        <section>
          <h3 className="text-3xl font-bold mb-6 text-center bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
            Media Collection
          </h3>

          {media.length > 0 ? (
            <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {media.map((m) => (
                <Link
                  key={m.id}
                  to={`/community/${communityId}/media/${m.id}`}
                  className="group bg-white/70 backdrop-blur-sm p-4 rounded-2xl border border-gray-200 shadow hover:shadow-xl transition hover:-translate-y-1"
                >
                  <div className="relative">
                    <div className="w-full h-40 bg-gray-100 flex items-center justify-center rounded-lg overflow-hidden">
                      {m.type === "image" ? (
                        <img
                          src={m.url}
                          alt={m.title}
                          className="object-cover h-full w-full group-hover:scale-105 transition-transform"
                        />
                      ) : (
                        <div className="text-gray-500 uppercase tracking-wider font-semibold">
                          {m.type}
                        </div>
                      )}
                    </div>
                    <div className="absolute bottom-2 right-2 text-xs bg-black/40 text-white px-2 py-0.5 rounded">
                      {m.type}
                    </div>
                  </div>

                  <div className="mt-3">
                    <h4 className="font-semibold text-indigo-700">{m.title}</h4>
                    <p className="text-sm text-gray-600">
                      {m.transcript ? "With transcript" : "No transcript"}
                    </p>
                  </div>
                </Link>
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-500 italic mt-8">
              No media available for this community yet.
            </p>
          )}
        </section>
      </Container>
    </div>
  );
}


