// --------------------------
// 5. Search and Filter Page
// --------------------------
export function SearchPage() {
  const [query, setQuery] = React.useState("");
  const results = React.useMemo(() => {
    if (!query) return DUMMY_COMMUNITIES;
    return DUMMY_COMMUNITIES.filter((c) =>
      c.name.toLowerCase().includes(query.toLowerCase())
    );
  }, [query]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-50">
      <TopNav />
      <Container>
        <h2 className="text-4xl font-extrabold mb-6 text-center bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
          Search Communities
        </h2>

        {/* Search Bar */}
        <div className="flex justify-center mb-10">
          <input
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="  Search communities or media..."
            className="w-full max-w-2xl p-4 rounded-full border border-gray-300 shadow-sm bg-white/70 backdrop-blur-sm focus:ring-2 focus:ring-indigo-400 outline-none text-gray-800 transition-all"
          />
        </div>

        {/* Results */}
        {results.length > 0 ? (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {results.map((c) => (
              <Link
                key={c.id}
                to={`/community/${c.id}`}
                className="group relative block rounded-2xl overflow-hidden shadow-sm hover:shadow-lg transition-all bg-white/80 backdrop-blur-sm border border-indigo-100"
              >
                <img
                  src={c.thumbnail}
                  alt={c.name}
                  className="h-44 w-full object-cover transition-transform duration-300 group-hover:scale-105"
                />
                <div className="p-4">
                  <h3 className="font-semibold text-lg text-gray-800 mb-1">
                    {c.name}
                  </h3>
                  <p className="text-sm text-gray-600 line-clamp-2">
                    {c.description}
                  </p>
                  <span className="text-indigo-600 text-sm font-medium mt-2 inline-block group-hover:underline">
                    View Details 
                  </span>
                </div>
              </Link>
            ))}
          </div>
        ) : (
          <div className="text-center text-gray-600 mt-16">
            No results found for <span className="font-semibold">"{query}"</span> 
          </div>
        )}
      </Container>
    </div>
  );
}


// --------------------------
// 6. Media Detail / Player Page
// --------------------------
export function MediaDetailPage() {
  const { communityId, mediaId } = useParams();
  const media =
    (DUMMY_MEDIA[communityId] || []).find((m) => m.id === mediaId) || null;

  if (!media) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-50 flex items-center justify-center">
        <div className="text-center bg-white/70 backdrop-blur-sm p-8 rounded-2xl shadow-lg">
          <h3 className="text-2xl font-semibold text-indigo-700 mb-2">
            Media not found
          </h3>
          <Link
            to={`/community/${communityId}`}
            className="text-indigo-500 hover:underline"
          >
             Back to Community
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-white to-indigo-50">
      <TopNav />
      <Container>
        {/* Header */}
        <div className="text-center mb-8">
          <h2 className="text-4xl font-extrabold bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
            {media.title}
          </h2>
          <p className="text-gray-600 mt-2">
            <span className="font-semibold text-indigo-600 uppercase">
              {media.type}
            </span>{" "}
             Part of <span className="italic">{communityId}</span> collection
          </p>
        </div>

        {/* Player Section */}
        <div className="bg-white/70 backdrop-blur-sm rounded-2xl shadow-lg border border-indigo-100 overflow-hidden mb-8">
          <div className="p-6 flex flex-col items-center justify-center">
            {media.type === "image" && (
              <img
                src={media.url}
                alt={media.title}
                className="w-full max-w-3xl rounded-lg shadow-md"
              />
            )}
            {media.type === "audio" && (
              <audio
                controls
                src={media.url}
                className="w-full max-w-2xl mt-4 rounded-lg"
              />
            )}
            {media.type === "video" && (
              <video
                controls
                src={media.url}
                className="w-full max-w-3xl rounded-lg shadow-md"
              />
            )}
          </div>
        </div>

        {/* Transcript Section */}
        {media.transcript && (
          <section className="bg-white/70 backdrop-blur-sm p-6 rounded-2xl shadow border border-gray-200">
            <h4 className="text-2xl font-semibold text-indigo-700 mb-3">
              Transcript
            </h4>
            <div className="bg-gray-50 border border-gray-100 rounded-lg p-4 text-sm leading-relaxed text-gray-700 whitespace-pre-line max-h-80 overflow-y-auto">
              {media.transcript}
            </div>
          </section>
        )}

        {/* Back Button */}
        <div className="text-center mt-8">
          <Link
            to={`/community/${communityId}`}
            className="inline-block bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-6 py-2 rounded-full font-medium shadow hover:scale-105 transition-all"
          >
             Back to Community
          </Link>
        </div>
      </Container>
    </div>
  );
}


