  const handleLogin = (e) => {
    e.preventDefault();
    // Replace with real authentication later
    navigate("/admin/dashboard");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-100 via-white to-purple-50">
      <form
        onSubmit={handleLogin}
        className="bg-white/80 backdrop-blur-sm p-10 rounded-2xl shadow-xl w-full max-w-sm border border-indigo-100"
      >
        <h3 className="text-3xl font-extrabold text-center mb-6 bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
          Admin Login
        </h3>

        <div className="space-y-4">
          <input
            placeholder="Email"
            type="email"
            required
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none"
          />
          <input
            placeholder="Password"
            type="password"
            required
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none"
