        </form>
      </Container>
    </div>
  );
}

export function AdminUploadMedia() {
  const navigate = useNavigate();

  const handleUpload = (e) => {
    e.preventDefault();
    // TODO: upload to server + save metadata
    navigate("/admin/dashboard");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-50">
      <TopNav isAdmin />
      <Container>
        <h2 className="text-4xl font-extrabold mb-8 text-center bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent">
          Upload Media
        </h2>

        <form
          onSubmit={handleUpload}
          className="max-w-2xl mx-auto bg-white/80 backdrop-blur-sm p-8 rounded-2xl shadow-xl border border-indigo-100 space-y-5"
        >
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">
              Select Community
            </label>
            <select className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none">
              <option value="">-- Select Community --</option>
              {DUMMY_COMMUNITIES.map((c) => (
                <option key={c.id} value={c.id}>
                  {c.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">
              Title
            </label>
            <input
              type="text"
              placeholder="e.g., Traditional Tattoo Art"
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">
              Media Type
            </label>
            <select className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none">
              <option value="image">Image</option>
              <option value="audio">Audio</option>
              <option value="video">Video</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">
              Upload File
            </label>
            <input
              type="file"
              className="w-full text-sm text-gray-700 bg-gray-50 border border-gray-300 rounded-lg p-2 cursor-pointer"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">
              Transcript / Notes
            </label>
            <textarea
              placeholder="Optional notes or transcript"
              rows="4"
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none resize-none"
            ></textarea>
          </div>

          <div className="flex justify-center pt-4">
            <button
              type="submit"
              className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-8 py-2 rounded-full font-semibold shadow hover:scale-105 transition-all"
            >
              Upload
            </button>
          </div>
        </form>
      </Container>
    </div>
  );
}


// --------------------------
// About page
// --------------------------
export function AboutPage() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-50">
      <TopNav />
      <Container>
        <section className="max-w-3xl mx-auto text-center bg-white/70 backdrop-blur-sm rounded-2xl p-10 shadow-md border border-indigo-100">
          <h2 className="text-4xl font-extrabold bg-gradient-to-r from-indigo-700 to-purple-600 bg-clip-text text-transparent mb-4">
            About Living Archives
          </h2>
          <p className="text-gray-700 text-lg leading-relaxed mb-6">
            <span className="font-semibold text-indigo-700">Living Archives</span> is a digital storytelling platform that 
            preserves the heritage of Sindhs diverse communities through immersive multimedia  
            including photographs, oral histories, audio-visual documentation, and 3D virtual tours.
          </p>

          <p className="text-gray-600 text-base leading-relaxed mb-6">
            The project is a <span className="font-medium text-purple-700">Kaavish</span> initiative, 
            designed and developed with the <span className="font-medium text-indigo-700">MERN stack</span>, 
            integrating modern web technologies to ensure accessibility, interactivity, and scalability.
          </p>

          <div className="flex justify-center space-x-4 mt-8">
            <Link
              to="/explore"
              className="px-5 py-2.5 rounded-full bg-indigo-600 text-white text-sm font-medium hover:bg-indigo-700 transition-all shadow-sm"
            >
              Explore Communities
            </Link>
            <Link
              to="/search"
              className="px-5 py-2.5 rounded-full border border-indigo-500 text-indigo-600 text-sm font-medium hover:bg-indigo-50 transition-all shadow-sm"
            >
              Search Archives
            </Link>
          </div>
        </section>

        <div className="mt-10 text-center text-gray-500 text-sm">
           {new Date().getFullYear()} Living Archives  A Kaavish Project
        </div>
      </Container>
    </div>
  );
}


// --------------------------
// Root App with routes (default export)
// --------------------------
export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/explore" element={<ExplorePage />} />
        <Route path="/search" element={<SearchPage />} />
        <Route path="/about" element={<AboutPage />} />

        <Route path="/community/:communityId" element={<CommunityPage />} />
        <Route path="/community/:communityId/media" element={<CommunityPage />} />
        <Route path="/community/:communityId/media/:mediaId" element={<MediaDetailPage />} />
        <Route path="/community/:communityId/tour" element={<VirtualTourPage />} />

        <Route path="/admin" element={<AdminLogin />} />
        <Route path="/admin/dashboard" element={<AdminDashboard />} />
        <Route path="/admin/communities/new" element={<AdminAddCommunity />} />
        <Route path="/admin/media/upload" element={<AdminUploadMedia />} />

        {/* Fallback */}
        <Route path="*" element={<HomePage />} />
      </Routes>
    </Router>
  );
}
